import React, { useState } from 'react';
import { Routes, Route, Link } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS
import './App.css'; // Custom CSS file for additional styling
import { isInstalled, getAddress } from '@gemwallet/api'; // Import the GemWallet API module

const App = () => {
  const [gemInstalled, setGemInstalled] = useState(false);
  const [userAddress, setUserAddress] = useState(null);

  const handleGemWalletLogin = () => {
    isInstalled().then((response) => {
      if (response.result.isInstalled) {
        getAddress().then((response) => {
          console.log(`Your address: ${response.result?.address}`);
          setUserAddress(response.result?.address);
        });
        setGemInstalled(true);
      }
    });
  };

  return (
    <div className="App">
      <header>
        <h1>XRPL Hosting as a Service</h1>
        {gemInstalled ? (
          <button className="btn btn-danger">Logout</button>
        ) : (
          <button className="btn btn-primary" onClick={handleGemWalletLogin}>
            Login
          </button>
        )}
      </header>

      <nav>
        <ul>
          <li>
            <Link to="/">Home</Link>
          </li>
          {gemInstalled && (
            <li>
              <Link to="/purchase">Purchase</Link>
            </li>
          )}
          {/* Add more navigation links as needed */}
        </ul>
      </nav>

      <main>
        <Routes>
          <Route path="/" element={<Home />} />
          {gemInstalled && <Route path="/purchase" element={<Purchase userAddress={userAddress} />} />}
          {/* Add more routes and components for other pages */}
        </Routes>
      </main>

      <footer>
        <p>Copyright Â© 2023 XRPL Hosting as a Service</p>
      </footer>
    </div>
  );
};

// Home component to display content
const Home = () => {
  return (
    <div>
      <h2>Welcome to XRPL Hosting as a Service</h2>
      <p>Please login with GemWallet to access XRPL hosting services.</p>
    </div>
  );
};

// Purchase component for users to buy instances/servers
const Purchase = ({ userAddress }) => {
  return (
    <div>
      <h2>Purchase Instances</h2>
      <p>Hello, your GemWallet address: {userAddress}</p>
      {/* Add instance selection and payment processing components */}
    </div>
  );
};

export default App;
