import React, { useState, useEffect } from 'react';
import { Routes, Route, Link } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS
import './App.css'; // Custom CSS file for additional styling
import { isInstalled, getAddress } from '@gemwallet/api'; // Import the GemWallet API module

// Function to format wallet address as abbreviation
const formatWalletAddress = (address) => {
  if (!address) return '';
  const firstFourChars = address.slice(0, 4);
  const lastFourChars = address.slice(-4);
  return `${firstFourChars}...${lastFourChars}`;
};

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userAddress, setUserAddress] = useState(null);
  const [loading, setLoading] = useState(false);

  // Function to handle GemWallet login
  const handleGemWalletLogin = () => {
    setLoading(true);
    isInstalled()
      .then((response) => {
        if (response.result.isInstalled) {
          getAddress()
            .then((response) => {
              const formattedAddress = formatWalletAddress(response.result?.address);
              console.log(`Your address: ${formattedAddress}`);
              setIsLoggedIn(true);
              setUserAddress(formattedAddress);
            })
            .catch((error) => {
              console.error('Error getting GemWallet Address:', error);
              setIsLoggedIn(false);
              setUserAddress(null);
            })
            .finally(() => {
              setLoading(false);
            });
        } else {
          alert('GemWallet needs to be installed: https://gemwallet.app');
          setLoading(false);
        }
      })
      .catch((error) => {
        console.error('Error checking GemWallet installation:', error);
        setLoading(false);
      });
  };

  // Function to handle logout
  const handleLogout = () => {
    setIsLoggedIn(false);
    setUserAddress(null);
  };

  // Add event listener to handle GemWallet extension close
  useEffect(() => {
    const handleMessageEvent = (event) => {
      if (event.data === 'gemwallet-closed') {
        setIsLoggedIn(false);
        setUserAddress(null);
      }
    };

    window.addEventListener('message', handleMessageEvent);

    return () => {
      window.removeEventListener('message', handleMessageEvent);
    };
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (!isLoggedIn) {
    return (
      <div className="App">
        <header>
          <h1>XRPL Hosting as a Service</h1>
          <button className="btn btn-primary" onClick={handleGemWalletLogin}>
            Login with GemWallet
          </button>
        </header>
      </div>
    );
  }

  return (
    <div className="App">
      <header>
        <h1>XRPL Hosting as a Service</h1>
        <button className="btn btn-danger" onClick={handleLogout}>
          Logout
        </button>
      </header>

      <nav>
        <ul>
          <li>
            <Link to="/">Home</Link>
          </li>
          <li>
            <Link to="/purchase">Purchase</Link>
          </li>
          {/* Add more navigation links as needed */}
        </ul>
      </nav>

      <main>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/purchase" element={<Purchase userAddress={userAddress} />} />
          {/* Add more routes and components for other pages */}
        </Routes>
      </main>

      <footer>
        <p>Copyright © 2023 XRPL Hosting as a Service</p>
      </footer>
    </div>
  );
};

// Home component to display content
const Home = () => {
  return (
    <div>
      <h2>Welcome to XRPL Hosting as a Service</h2>
      <p>Please login with GemWallet to access XRPL hosting services.</p>
    </div>
  );
};

// Purchase component for users to buy instances/servers
const Purchase = ({ userAddress }) => {
  return (
    <div>
      <h2>Purchase Instances</h2>
      <p>Hello, your GemWallet address: {userAddress}</p>
      {/* Add instance selection and payment processing components */}
    </div>
  );
};

export default App;
