import React, { useState, useEffect } from 'react';
import { Routes, Route, Link } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS
import './App.css'; // Custom CSS file for additional styling
import { isInstalled, getAddress } from '@gemwallet/api'; // Import the GemWallet API module

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userAddress, setUserAddress] = useState(null);

  useEffect(() => {
    // Check if the user is logged in
    const loggedInUser = checkLoggedInUser();
    setIsLoggedIn(loggedInUser);

    // Check if GemWallet extension is installed
    const isGemWalletInstalled = checkGemWalletInstallation();

    if (loggedInUser && isGemWalletInstalled) {
      // Trigger getAddress function from GemWallet API
      triggerGetAddress();
    }

    // Add event listener for GemWallet extension close event
    const handleMessageEvent = (event) => {
      if (event.data === 'gemwallet-closed') {
        setIsLoggedIn(false);
        setUserAddress(null);
      }
    };

    window.addEventListener('message', handleMessageEvent);

    return () => {
      window.removeEventListener('message', handleMessageEvent);
    };
  }, []);

  // Function to check if the user is logged in
  const checkLoggedInUser = () => {
    // Replace this with your actual logic to check if the user is logged in
    // For example, you can use localStorage or session storage to store login status
    // Return true if the user is logged in, false otherwise
    const isLoggedIn = true; // Replace this with your actual logic
    return isLoggedIn;
  };

  // Function to check if GemWallet extension is installed
  const checkGemWalletInstallation = async () => {
    try {
      const installed = await isInstalled(); // Call isInstalled() method
      console.log('Is GemWallet installed?', installed);
      return installed;
    } catch (error) {
      console.error('Error checking GemWallet installation:', error);
      return false;
    }
  };

  // Function to trigger getAddress function from GemWallet API
  const triggerGetAddress = () => {
    getAddress()
      .then((response) => {
        console.log('GemWallet Address:', response.address);
        setUserAddress(formatWalletAddress(response.address));
      })
      .catch((error) => {
        console.error('Error getting GemWallet Address:', error);
      });
  };

  // Function to format wallet address as abbreviation
  const formatWalletAddress = (address) => {
    if (!address) return '';
    const firstFourChars = address.slice(0, 4);
    const lastFourChars = address.slice(-4);
    return `${firstFourChars}...${lastFourChars}`;
  };

  // Function to handle GemWallet login button click
  const handleGemWalletLogin = () => {
    isInstalled()
      .then((response) => {
        if (response.result.isInstalled) {
          triggerGetAddress();
          setIsLoggedIn(true);
        } else {
          alert('GemWallet needs to be installed: https://gemwallet.app');
        }
      })
      .catch((error) => {
        console.error('Error checking GemWallet installation:', error);
      });
  };

  // Function to handle GemWallet logout button click
  const handleGemWalletLogout = () => {
    setIsLoggedIn(false);
    setUserAddress(null);
  };

  return (
    <div className="App">
      <header>
        <h1>XRPL Hosting as a Service</h1>
        {isLoggedIn ? (
          <button className="btn btn-danger" onClick={handleGemWalletLogout}>
            Logout
          </button>
        ) : (
          <button className="btn btn-primary" onClick={handleGemWalletLogin}>
            Login
          </button>
        )}
      </header>

      <nav>
        <ul>
          <li>
            <Link to="/">Home</Link>
          </li>
          {isLoggedIn && (
            <li>
              <Link to="/purchase">Purchase</Link>
            </li>
          )}
          {/* Add more navigation links as needed */}
        </ul>
      </nav>

      <main>
        <Routes>
          <Route path="/" element={<Home />} />
          {isLoggedIn && <Route path="/purchase" element={<Purchase userAddress={userAddress} />} />}
          {/* Add more routes and components for other pages */}
        </Routes>
      </main>

      <footer>
        <p>Copyright Â© 2023 XRPL Hosting as a Service</p>
      </footer>
    </div>
  );
};

// Home component to display content
const Home = () => {
  return (
    <div>
      <h2>Welcome to XRPL Hosting as a Service</h2>
      <p>Please login to access XRPL hosting services.</p>
    </div>
  );
};

// Purchase component for users to buy instances/servers
const Purchase = ({ userAddress }) => {
  return (
    <div>
      <h2>Purchase Instances</h2>
      {userAddress ? (
        <p>Hello, your GemWallet address: {userAddress}</p>
      ) : (
        <p>Please login to purchase instances.</p>
      )}
      {/* Add instance selection and payment processing components */}
    </div>
  );
};

export default App;
