import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import { isInstalled, getAddress } from '@gemwallet/api';

function App() {
  const [gemWalletInstalled, setGemWalletInstalled] = useState(false);

  const handleConnect = () => {
    isInstalled().then((response) => {
      if (response.result.isInstalled) {
        getAddress().then((response) => {
          console.log(`Your address: ${response.result?.address}`);
        });
      }
    });
  };

  useEffect(() => {
    // Check if GemWallet is installed on component mount
    isInstalled().then((response) => {
      setGemWalletInstalled(response.result.isInstalled);
    });
  }, []);

  return (
    <div className="App d-flex flex-column justify-content-between">
      {/* Top Fold */}
      <div className="d-flex flex-column align-items-center mt-5">
        <h1>My App</h1>
        {gemWalletInstalled ? null : (
          <button onClick={handleConnect} className="btn btn-primary mt-4">
            Click me!
          </button>
        )}
      </div>

      {/* Main Content */}
      {gemWalletInstalled && <div className="full-width-div">Your web page content here</div>}

      {/* Footer */}
      <footer className="bg-dark text-white text-center py-2 fixed-bottom" style={{ height: '60px' }}>
        {/* Add navigation links here */}
      </footer>
    </div>
  );
}

export default App;
