import React, { useEffect } from 'react';
import { Routes, Route, Link } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS
import './App.css'; // Custom CSS file for additional styling
import { isInstalled, getAddress } from '@gemwallet/api'; // Import the GemWallet API module

const App = () => {
  useEffect(() => {
    // Check if the user is logged in
    const isLoggedIn = checkLoggedInUser();
    // Check if GemWallet extension is installed
    const isGemWalletInstalled = checkGemWalletInstallation();

    if (isLoggedIn && isGemWalletInstalled) {
      // Trigger getAddress function from GemWallet API
      triggerGetAddress();
    }
  }, []);

  // Function to check if the user is logged in
  const checkLoggedInUser = () => {
    // Replace this with your actual logic to check if the user is logged in
    // For example, you can use localStorage or session storage to store login status
    // Return true if the user is logged in, false otherwise
    const isLoggedIn = true; // Replace this with your actual logic
    return isLoggedIn;
  };

  // Function to check if GemWallet extension is installed
  const checkGemWalletInstallation = async () => {
    try {
      const installed = await isInstalled(); // Call isInstalled() method
      console.log('Is GemWallet installed?', installed);
      return installed;
    } catch (error) {
      console.error('Error checking GemWallet installation:', error);
      return false;
    }
  };

  // Function to trigger getAddress function from GemWallet API
  const triggerGetAddress = () => {
    getAddress()
      .then((response) => {
        console.log('GemWallet Address:', response.address);
      })
      .catch((error) => {
        console.error('Error getting GemWallet Address:', error);
      });
  };

  return (
    <div className="App">
      <header>
        <h1>XRPL Hosting as a Service</h1>
        <button className="btn btn-primary">Login</button>
      </header>

      <nav>
        <ul>
          <li>
            <Link to="/">Home</Link>
          </li>
          <li>
            <Link to="/purchase">Purchase</Link>
          </li>
          {/* Add more navigation links as needed */}
        </ul>
      </nav>

      <main>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/purchase" element={<Purchase />} />
          {/* Add more routes and components for other pages */}
        </Routes>
      </main>

      <footer>
        <p>Copyright © 2023 XRPL Hosting as a Service</p>
      </footer>
    </div>
  );
};

// Home component to display content
const Home = () => {
  return (
    <div>
      <h2>Welcome to XRPL Hosting as a Service</h2>
      <p>Please login to access XRPL hosting services.</p>
    </div>
  );
};

// Purchase component for users to buy instances/servers
const Purchase = () => {
  return (
    <div>
      <h2>Purchase Instances</h2>
      <p>Please login to purchase instances.</p>
    </div>
  );
};

export default App;
